(function ($, hx) {
    'use strict';

    $(function () {
        $.ajax({
            url: hx.url('users', true),
            data: {current: true},
            dataType: 'json',
            success: function (data) {
                hx.data.user = new hx.User(data);
            }
        });
    });

}(window.$, window.hx));
